import React, { useState, useEffect, Fragment } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState({ data: [] });
  const [changes, setChanges] = useState("2019-11-28");
  const [url, setUrl] = useState(
    `https://api.coindesk.com/v1/bpi/historical/close.json?currency=EUR&start=2019-12-01&end=2019-12-10`
  );
  //`https://api.coindesk.com/v1/bpi/historical/close.json?currency=EUR&start=${chages}&end=2019-12-13`

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios(url);
      setData(response);
    };
    fetchData();
  }, [url]);
  console.log(
    "object",
    data.data.bpi ? Object.keys(data.data.bpi) : " ",
    data.data.bpi,
    url
  );
  const handleTable = items => {
    <table>
      <thead>
        <tr>
          <th>#</th>
          <th>Date</th>
          <th>Bit coin course</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>1</td>
          <td>Mark</td>
          <td>Otto</td>
          <td>@mdo</td>
        </tr>
        <tr>
          <td>2</td>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
        </tr>
        <tr>
          <td>3</td>
          <td colSpan="2">Larry the Bird</td>
          <td>@twitter</td>
        </tr>
      </tbody>
    </table>;
  };
  return (
    <div>
      <h1>the new app works</h1>
      <Fragment>
        <input
          type="text"
          value={changes}
          onChange={event => setChanges(event.target.value)}
        />
        <button
          type="button"
          onClick={() =>
            setUrl(
              `https://api.coindesk.com/v1/bpi/historical/close.json?currency=EUR&start=${changes}&end=2019-12-13`
            )
          }
        >
          Search
        </button>
        <div>
          {!!data.data.bpi && (
            <ul>
              {Object.keys(data.data.bpi).map(element => {
                return (
                  <>
                    <li>
                      {element}
                      {":  "}
                      {data.data.bpi[element]}
                    </li>
                  </>
                );
              })}
            </ul>
          )}
        </div>
      </Fragment>
    </div>
  );
}

export default App;
