import React, { useState, useEffect, Fragment } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState({ data: [] });
  const [changes, setChanges] = useState("2019-11-28");
  const [url, setUrl] = useState(
    `https://api.coindesk.com/v1/bpi/historical/close.json?currency=EUR&start=2019-12-01&end=2019-12-10`
  );
  //`https://api.coindesk.com/v1/bpi/historical/close.json?currency=EUR&start=${chages}&end=2019-12-13`
  const [isloading, setLaod] = useState(flase);
  useEffect(() => {
    const fetchData = async () => {
      const response = await axios(url);
      setData(response);
    };
    fetchData();
  }, [url]);
  console.log(
    "object",
    data.data.bpi ? Object.keys(data.data.bpi) : " ",
    data,
    url
  );
  const handleTable = items => {
    return (
      <table className="ui celled table">
        <thead>
          <tr>
            <th>Date</th>
            <th>courses</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(items).map(item => {
            return (
              <tr>
                <td key={item}>{item}</td>{" "}
                <td key={items[item]}>{items[item]}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  return (
    <div>
      <h1>the new app works</h1>
      <Fragment>
        <div className="ui input focus" style={{ marginBottom: "20px" }}>
          <input
            type="text"
            value={changes}
            onChange={event => setChanges(event.target.value)}
          />
          <button
            className="ui button"
            type="button"
            onClick={() =>
              setUrl(
                `https://api.coindesk.com/v1/bpi/historical/close.json?currency=EUR&start=${changes}&end=2019-12-13`
              )
            }
          >
            Search
          </button>
        </div>
        <div>{!!data.data.bpi && handleTable(data.data.bpi)}</div>
      </Fragment>
    </div>
  );
}

export default App;
